import javax.swing.*;import java.awt.*;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;public class InicioGU extends JFrame {    private static final int MAXIMA_OPCION_MENU = 6;    public InicioGU() {        setTitle("Interfaz Gr√°fica Roy");        setSize(400, 500);        setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);        JPanel panel = new JPanel();        panel.setLayout(new GridLayout(7, 1));        JButton btnVerAnimales = new JButton("Ver animales agregados");        JButton btnEliminarAnimal = new JButton("Eliminar 1 animal agregado");        JButton btnOrdenarAscendente = new JButton("Ordenar de manera ascendente");        JButton btnOrdenarDescendente = new JButton("Ordenar de manera descendente");        JButton btnAgregarAnimal = new JButton("Agregar animal a la lista");        JButton btnSalir = new JButton("Salir");        btnVerAnimales.addActionListener(new ActionListener() {            public void actionPerformed(ActionEvent e) {                Lista.listadoAnimales();            }        });        btnEliminarAnimal.addActionListener(new ActionListener() {            public void actionPerformed(ActionEvent e) {                Lista.eliminarAnimal();            }        });        btnOrdenarAscendente.addActionListener(new ActionListener() {            public void actionPerformed(ActionEvent e) {                Lista.ordenarAnimales();            }        });        btnOrdenarDescendente.addActionListener(new ActionListener() {            public void actionPerformed(ActionEvent e) {                Lista.ordenarAnimalesreversa();            }        });        btnAgregarAnimal.addActionListener(new ActionListener() {            public void actionPerformed(ActionEvent e) {                Lista.agregarAnimal();            }        });        btnSalir.addActionListener(new ActionListener() {            public void actionPerformed(ActionEvent e) {                System.out.println("Hasta pronto");                System.exit(0);            }        });        panel.add(new JLabel("QUIZ"));        panel.add(btnVerAnimales);        panel.add(btnEliminarAnimal);        panel.add(btnOrdenarAscendente);        panel.add(btnOrdenarDescendente);        panel.add(btnAgregarAnimal);        panel.add(btnSalir);        add(panel);    }    public static void main(String[] args) {        SwingUtilities.invokeLater(new Runnable() {            public void run() {                new InicioGU().setVisible(true);            }        });    }}