import java.util.*;public class Lista {    static ArrayList<Animales> crearLista(String contactos){        ArrayList<Animales> listaContactos= new ArrayList<>();        String[] contacto=contactos.split(";");        Animales animal;        String[] atributos;        for (int i = 0; i < contacto.length; i++) {            atributos=contacto[i].split(",");            animal= new Animales(atributos[0],atributos[1],Integer.parseInt(atributos[2]),Float.parseFloat(atributos[3]));            listaContactos.add(animal);        }        return listaContactos;    }    public static Object listadoAnimales() {        ArrayList<Animales> listaAnimales = new ArrayList<>();        Animales animal1 = new Animales("gato","negro", 5,23.8);        Animales animal2 = new Animales("Loro","Verde", 58,3.9);        Animales animal3 = new Animales("Perro","Blnaco", 15,83.9);        listaAnimales.add(animal1);        listaAnimales.add(animal2);        listaAnimales.add(animal3);        for (Animales r: listaAnimales) {            System.out.println("el animal " + r + " a sido agregado ") ;        }        return listaAnimales;    }    public static Object eliminarAnimal(){        ArrayList<Animales> listaAnimales = new ArrayList<>();        Animales animal1 = new Animales("gato","negro", 5,23.6);        Animales animal2 = new Animales("Loro","Verde", 58,3.7);        Animales animal3 = new Animales("Perro","Blnaco", 15,83.9);        listaAnimales.add(animal1);        listaAnimales.add(animal2);        listaAnimales.add(animal3);        listaAnimales.remove(0);        for (Animales r: listaAnimales) {            System.out.println("el animal " + r + " a sido eliminado ") ;        }        return listaAnimales;    }    public static Object ordenarAnimales(){        ArrayList<Animales> listaAnimales = new ArrayList<>();        Animales animal1 = new Animales("gato","negro", 5,23.5);        Animales animal2 = new Animales("Loro","Verde", 58,3.6);        Animales animal3 = new Animales("vaca","Blanco", 15,83.7);        listaAnimales.add(animal1);        listaAnimales.add(animal2);        listaAnimales.add(animal3);        Collections.sort(listaAnimales,Comparator.comparing(Animales::getNombre));        for (Animales r: listaAnimales) {            System.out.println("la nueva lista ordenada es:  " + r) ;        }        return listaAnimales;    }    public static Object ordenarAnimalesreversa(){        ArrayList<Animales> listaAnimales = new ArrayList<>();        Animales animal1 = new Animales("gato","negro", 5,23.3);        Animales animal2 = new Animales("Loro","Verde", 58,3.4);        Animales animal3 = new Animales("vaca","Blanco", 15,83.8);        listaAnimales.add(animal1);        listaAnimales.add(animal2);        listaAnimales.add(animal3);        Collections.sort(listaAnimales,Comparator.comparing(Animales::getNombre).reversed());        for (Animales r: listaAnimales) {            System.out.println("la nueva lista ordenada es:  " + r) ;        }        return listaAnimales;    }    public static void agregarAnimal(){        ArrayList<Animales> listaAnimales = new ArrayList<>();        Animales animal1 = new Animales("gato","negro", 5,23.0);        Animales animal2 = new Animales("Loro","Verde", 58,3.0);        Animales animal3 = new Animales("vaca","Blanco", 15,83.0);        listaAnimales.add(animal1);        listaAnimales.add(animal2);        listaAnimales.add(animal3);        for (Animales r: listaAnimales) {            System.out.println("los datos de su lista son  " + r) ;        }        System.out.println("-->-->-->-->--> <--<--<--<--<--");        Scanner entrada = new Scanner(System.in);        System.out.println("Ingresa los detalles de la nueva instancia de animal " +                "separados por coma (nombre, color, edad, peso): ");        String input = entrada.nextLine();        String []inputs = input.split(",");        String newNombre = inputs[0].trim();        String newColor = inputs[1].trim();        int newPeso = Integer.parseInt(inputs[2].trim());        double newAltura = Double.parseDouble(inputs[3].trim());        Animales newAnimal = new Animales(newNombre,newColor,newPeso,newAltura);        listaAnimales.add(newAnimal);        System.out.println("Lista actualizada de animales");        for (Animales animales: listaAnimales){            System.out.println(animales.toString());        }    }}