import java.io.*;public class leerArchivo {    public static void main(String[] args) {        String archivoPlano = "C:\\Users\\RoyMR\\Documents\\POO2-2023\\UNAC_POO2_Q1\\src\\listado.txt";        try {            FileReader fileReader = new FileReader(archivoPlano);            BufferedReader bufferedReader = new BufferedReader(fileReader);            String linea;            while ((linea = bufferedReader.readLine()) != null) {                System.out.println(linea);            }            bufferedReader.close();        } catch (IOException e) {            System.out.println("Error tipo IO");        }    }}